SELECT payment_type,count() AS trip_count,formatReadableQuantity(sum(trip_distance)::Float64) AS total_distance,formatReadableQuantity(sum(total_amount)::Float64) AS total_amount_sum,formatReadableQuantity(sum(tip_amount)::Float64) AS tip_amount_sum FROM nyc_taxi WHERE pickup_datetime >= '2009-01-01' AND pickup_datetime < '2009-04-01' GROUP BY payment_type ORDER BY trip_count DESC;
